  `PhuDeChuyenNhuong` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `ImageChuyenNhuong` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `VideoChuyenNhuong` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `ChiTietChuyenNhuong` text COLLATE utf8_unicode_ci NOT NULL,
  `NgayDangChuyenNhuong` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

