CREATE TABLE `tintucquanvot` (
  `MaTinTucQuanVot` int(10) NOT NULL,
  `TieuDeQuanVot` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `PhuDeQuanVot` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `ImageQuanVot` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `VideoQuanVot` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `ChiTietQuanVot` text COLLATE utf8_unicode_ci NOT NULL,
  `NgayDangQuanVot` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- Dumping data for table `tintucquanvot`
